import React, { useEffect, useState } from "react";
import {
  Box,
  Typography,
  Grid,
  Card,
  CardMedia,
  CardContent,
  Button,
} from "@mui/material";
import { keyframes } from "@emotion/react";

// Keyframe animation for fade-in effect
const fadeInUp = keyframes`
  from {
    opacity: 0;
    transform: translateY(50px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

// Mock Data for Projects
const projects = [
  {
    title: "Tetma Health care",
    description:
      "It empowers users to manage their health conveniently and securely, bridging the gap between patients and healthcare providers.",
    image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTTra8coCVzAoqhTrRlfqGWBMmzQWLxWItv2g&s", // Replace with your image URL
    url: "https://github.com/Abra313/TETMA-HEALTH-CARE", // Project URL
  },
  {
    title: "Bankorama/Swift fund",
    
    description:
      "A secure and modern banking website for seamless transactions, financial management, and intuitive user experiencesâ€”empowering users to manage finances anytime, anywhere.",
    image: "data:image/jpeg;base64,",
    url: "https://bankorama-bank.web.app/",
  },
  {
    title: "Go-Ride",
    
    description:
      "Go-Ride connects riders with reliable drivers for safe, affordable, and efficient transportation, offering real-time tracking and 24/7 availability.",
    image: "data:image/jpeg;base64,",
    url: "https://go-rideng.netlify.app/",
  },
  {
    title: "Net World",
    description:
      "A user-friendly social media app for seamless connections, sharing, and real-time communication.",
    image: "data:image/jpeg;base64,",
    url: "https://example.com/project4",
  },
];

const ProjectPage = () => {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      const scrollPosition = window.scrollY + window.innerHeight;
      const triggerHeight = document.getElementById("projects").offsetTop;

      if (scrollPosition > triggerHeight) {
        setIsVisible(true);
      }
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <Box
      id="projects"
      sx={{
        backgroundColor: "#f7f5f0",
        padding: "50px 20px",
        minHeight: "100vh",
      }}
    >
      <Typography
        variant="h3"
        fontWeight="bold"
        textAlign="center"
        mb={4}
        mt={2}
        color="#333"
      >
        My Projects
      </Typography>

      <Grid container spacing={4} justifyContent="center">
        {projects.map((project, index) => (
          <Grid
            item
            xs={12}
            sm={12}
            md={5}
            lg={5}
            key={index}
            sx={{
              display: "flex",
              justifyContent: "center",
              animation: isVisible
                ? `${fadeInUp} 0.8s ease-out ${index * 0.2}s forwards`
                : "none",
              opacity: 0,
            }}
          >
            <Card
              sx={{
                display: "flex",
                flexDirection: "row",
                width: "100%",
                maxWidth: "800px",
                borderRadius: 2,
                boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
                overflow: "hidden",
                backgroundColor: "#fff",
              }}
            >
              {/* Left Section: Project Info */}
              <CardContent
                sx={{
                  width: "50%",
                  display: "flex",
                  flexDirection: "column",
                  justifyContent: "center",
                  padding: "24px",
                }}
              >
                <Typography
                  variant="h5"
                  fontWeight="bold"
                  color="#1565c0"
                  mb={1}
                >
                  {project.title}
                </Typography>
                <Typography
                  variant="subtitle1"
                  fontWeight="600"
                  color="textSecondary"
                  mb={2}
                >
                  {project.role}
                </Typography>
                <Typography
                  variant="body1"
                  color="textSecondary"
                  sx={{ lineHeight: "1.6", marginBottom: 2 }}
                >
                  {project.description}
                </Typography>
                {/* View Button */}
                <Button
                  variant="contained"
                  href={project.url}
                  target="_blank"
                  sx={{
                    backgroundColor: "#1565c0",
                    color: "#fff",
                    "&:hover": {
                      backgroundColor: "#0d47a1",
                    },
                  }}
                >
                  View
                </Button>
              </CardContent>

              {/* Right Section: Project Image */}
              <CardMedia
                component="img"
                image={project.image}
                alt={project.title}
                sx={{
                  width: "50%",
                  objectFit: "cover",
                }}
              />
            </Card>
          </Grid>
        ))}
      </Grid>
    </Box>
  );
};

export default ProjectPage;
